mapper.select:

select

from mmall_user
where username = #{username}
and password = #{password}

select question
from mmall_user
where username=#{username}

select count(1)
from mmall_user
where username=#{username}
and question=#{question}
and answer=#{answer}

mapper.update:
update mmall_user
set password=#{passwordNew},update_time=now()
where username=#{username}

mapper.select:
select count(1)
from mmall_user
where id=#{userId}
and password=#{password}


select count(1)
from mmall_user
where id!=#{userId}
and email=#{email}
